      return (
        <>
        
            <div id="title-Addpost">

                <h1 id="post-title">Posts</h1>

                <input type="text" placeholder="Search Post..." value={searchTerm}
                 onChange = {(event) => setSearchTerm(event.target.value)}></input>

                <Link to ="/posts/add" className="addlink">Add Post</Link>
                

            </div>

            <div id="main">
                <Route path="/posts/add">
                    <HandleCreatePost posts={posts} setPosts={setPosts}/>
                </Route>

                
            </div>


          {
              
            posts.map((post) => (

            <div key={post._id}>  
              <h2>{post.title}</h2> 
              <p>{post.description}</p>
              <h3>Price: {post.price}</h3>
              <h3>Seller: {post.author.username}</h3>
              <h3>Location: {post.location}</h3>



              {/* <h3>Message: {post.messages}</h3> */}
              {/* <h3>{post.createdAt}</h3>
              <h3>{post.updatedAt}</h3> */}

              
              <button disabled={post.isAuthor ? true : false} className="sendMessage" onClick={() => setPostId(post._id)}>Send Message</button>

              <button disabled={post.isAuthor ? false : true} className="delete-btn" onClick={() => handleDelete(post._id)}>Delete</button>



              {
                  
                    postId.length ? <SendMessage posts={posts} setPosts={setPosts} postId={postId} setPostId={setPostId} /> : ""
              }
  
              
            </div>)
            )}

                {/* <SendMessage posts={posts} setPosts={setPosts} postId={postId} setPostId={setPostId} /> */}
            


        </>
      )  
    
}

