import React, { useState } from 'react';
// import AuthenticatedPosts from '../components/AuthenticatedPosts';

const HandleCreatePost = ({posts, setPosts}) => {

    const [newPost, SetNewPost] = useState([]);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [location, setLocation] = useState("");
    const [willDeliver, setWillDeliver] = useState(false);
    

    const handleSubmit = async (event) => {
        event.preventDefault();
        const token = localStorage.getItem("userToken")
        // console.log('handleCreatePost token', token);
      
            const response = await fetch("https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts", {
                method: 'POST',
                headers: {
                    'Content-Type': 'Application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    post: { 
                        title: title,
                        description: description,
                        price: price,
                        location: location,
                        willDeliver: willDeliver,

                    }
                })
            }).then(response => response.json())
              .then(result => {
                  console.log(result);
                  SetNewPost(result);
              })
              .catch(console.error);

            // const arr = [1,2,3,4,5]; // prev state
            // const arr2 = [1,2,3]; // new state

            // // combine the two states

            // arr.concat(arr2)

           const latestPost = posts.concat(newPost);
            setPosts(latestPost);
            setTitle("");
            setDescription("");
            setPrice("");
            setLocation("");
            setWillDeliver(false);

            // {
            //     <AuthenticatedPosts />
            // }
        
    }

    return (
      <>
        <h3>Create Post</h3>
  
        <form onSubmit={handleSubmit}>

          <input type="text" placeholder = "title" value={title}
           onChange = {(event) => {setTitle(event.target.value)}}></input> 
  
          <input type="text" placeholder = "description" value={description}
           onChange = {(event) => {setDescription(event.target.value)}}></input>  
  
          <input type="text" placeholder = "price" value={price}
           onChange = {(event) => {setPrice(event.target.value)}}></input>
  
          <input type="text" placeholder = "location" value={location}
           onChange = {(event) => {setLocation(event.target.value)}}></input>
  
          <input type="checkbox" value="Deliver"
           onClick = {() => setWillDeliver(true)}></input>
  
           <label htmlFor="willDeliver">Willing to Deliver?</label> <br></br>
  
           <button type="submit" className="submitPost-Btn">Submit</button>     
  
        </form>
      </>
    )
  }

  export default HandleCreatePost;
