import React, { useState } from 'react';
import LoginForm from './LoginForm';

const RegisterForm = () => {
    const [username, setUserName] = useState(""); 
    const [password, setPassword] = useState("");
    const [confirmation, setConfirmation] = useState("");
    const [localStorage, setLocalStorage] = useState(""); 

    const handleSubmit = async(event) => {
        event.preventDefault();
        if (password !== confirmation) {
            alert ("Password doesn't match")
            setPassword("")
            setConfirmation("")
        } else {
            const response = await fetch('https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts', {
                method: 'POST',
                headers: {
                    'Content-Type' : 'Application/json',
                },
                body: JSON.stringify({username, password})
            }); 
            const result = await response.json();
            //console.log(result.data.posts)
            setLocalStorage(result);
            setUserName("");
            setPassword("");
            setConfirmation("");
        }
        result ? <LoginForm /> : console.log("Error occurs")
    
    }
    return (
        <>
            <h3>Register Form</h3>

            <form onSubmit={handleSubmit}>
                <input type = "text" placeholder = "Username" value={username}
                 onChange = {(event) => {setUserName(event.target.value)}}
                 minLength={4} required>
                </input>

                <input type = "password" placeholder = "Password" value={password}
                 onChange = {(event) => {setPassword(event.target.value)}}
                  minLength={6} required>
                </input>

                <input type = "password" placeholder = "Password Confirmation" value={confirmation}
                 onChange = {(event) => setConfirmation(event.target.value)}
                 required>
                 </input>

                 <br></br>

                 <button type = "submit" className = "submit-Btn">Submit</button>
            </form>
        </>
    )
}

export default RegisterForm;
