import React, { useState } from 'react';

const SendMessage = ({posts, setposts, postId, setPostId}) => {
    const [content, setContent] = useState("");
    // console.log("postID", postId);

    const handleMessageSubmit = async(event) => {
        let messageInfo = 
                            { message : {     
                                content: `${content}`
        }}
        console.log(messageInfo);
            event.preventDefault();
            const token = localStorage.getItem("userToken")

        const response = await fetch(`https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts/${postId}/messages`, {
            method: 'POST',
            headers: {
                'Content-Type': 'Applicaiton/json',
                'Authorization' : `Bearer ${token}`
            },
            body: JSON.stringify(
                messageInfo
            )
        }).then(response => response.json())
          .then(result => {
              console.log(result);
              

              let allPosts = posts;
              let updatedMessages = result;

                for(let i = 0; i < allPosts.length; i++){
                    if (allPosts[i]._id === postId){
                        allPosts[i].messages = updatedMessages
                    }
                }
            
              setposts(allPosts);
              setContent("");
              setPostId(null);
          
          })
          .catch(console.error);




    }
    
    return (
        <form onSubmit={handleMessageSubmit}>
            <textarea rows="4" cols="30" value={content} placeholder="Message..."
            onChange = {(event) => setContent(event.target.value)}></textarea>

            <button type="submit" className="msg-Submit-Btn">Submit</button>
        </form>
    )
}


export default SendMessage;
