import React, { useState, useEffect} from 'react';

const Post = (props) => {

    const [posts, setPosts] = useState([]);
  
    useEffect( () => {
        const fetchPost = async () => {
            const BASE_URL = 'https://strangers-things.herokuapp.com';
            const cohortName = '2110-VPI-WEB-PT';
  
            const response = await fetch(`${BASE_URL}/api/${cohortName}/posts`); // fetch all the posts
            const result = await response.json();
            console.log(result.data.posts);
            setPosts(result.data.posts);
        }
        fetchPost();
  }, [])
  
  return (
    <>
        <h1>Posts View</h1>
        {posts.map((post) => (

            <div key={post._id}>
                 <p>{post.location}</p>
                 <p>{post.willDeliver}</p>
                 <p>{post.message}</p>

                 <p>{post.active}</p>
                 <p>{post._id}</p>
                 <p>{post.author._id}</p>
                 <p>{post.author.username}</p>

                 <p>{post.cohort}</p>
                 <p>{post.title}</p>
                 <p>{post.description}</p>
                 <p>{post.price}</p>
                 <p>{post.createdAt}</p>
                 <p>{post.updatedAt}</p>
                 <p>{post._v}</p>
                 <p>{post.isAuthor}</p>

                 
            </div>
         
        ))}

    </>
  )
  }

  export default Post;
