import React, { useState } from 'react';

const AuthenticatedPosts = async({posts, setPosts}) => {
    const response = await fetch("https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts")
      .then(response => response.json())
      .then(result => {
        console.log(result.data.posts);
        setPosts(result.data.posts);
      })
      .catch(console.error);
  
    return (
      <>
        <h1>Posts</h1>

        <button type="button" className="add-Btn"
        onClick={handleAddPost}>Add Post</button>
        {
          posts.map(post => <div key={post._id}>  
            <h3>{post.title}</h3>
            <h3>{post.description}</h3>
            <h3>{post.price}</h3>
            <h3>{post.location}</h3>
            <h3>{post.willDeliver}</h3>
            <h3>{post.createdAt}</h3>
            <h3>{post.updatedAt}</h3>
  
          </div>)
        }
      </>
    )  
  
  }

  const handleAddPost = ({posts, setPosts}) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [location, setLocation] = useState("");
    const [deliver, setDeliver] = useState(false);
  
  
    const postSubmit = async () => {
      const response = await fetch("https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts", {
      method: 'POST',
      headers: {
        'Content-Type': 'Application/json',
      },
      body: JSON.stringify({title, description, price})
    });
    const result = await response.json();
    const data_Posts = result.data.posts;
    
    setPosts(data_Posts);
  
    }
    return (
      <>
        <h3>Create Post</h3>
  
        <form onSubmit={postSubmit}>
          <input type="text" placeholder = "title" value={title}
           onChange = {(event) => {setTitle(event.target.value)}}></input> 
  
          <input type="text" placeholder = "description" value={description}
           onChange = {(event) => {setDescription(event.target.value)}}></input>  
  
          <input type="text" placeholder = "price" value={price}
           onChange = {(event) => {setPrice(event.target.value)}}></input>
  
          <input type="text" placeholder = "location" value={location}
           onChange = {(event) => {setLocation(event.target.value)}}></input>
  
          <input type="checkbox" value="Deliver"
           onClick = {() => setDeliver(true)}></input>
  
           <label for="willDeliver">Willing to Deliver?</label> <br></br>
  
           <button type="submit" className="submitPost-Btn">Submit</button>     
  
        </form>
      </>
    )
  }

export default AuthenticatedPosts;
