import React, { useState, useEffect } from 'react';

const AuthenticatedPosts = ({posts, setPosts}) => {

    useEffect(() => {
        const fetchPosts = async () => {
            const response = await fetch("https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts")
                .then(response => response.json())
                .then(result => {
                    console.log(result.data.posts);
                    setPosts(result.data.posts);
                })
                .catch(console.error);
        }
        fetchPosts();
    }, [])
    
      return (
        <>
          <h1>Posts</h1>
          
          <button type="button" className="add-Btn"
           onClick={handleAddPost}>Add Post</button>

          {
            posts.map((post) => (

            <div key={post._id}>  
              <h2>{post.title}</h2>
              <p>{post.description}</p>
              <h3>Price: {post.price}</h3>
              <h3>Seller: {post.author.username}</h3>
              <h3>Location: {post.location}</h3>
              <h3>{post.willDeliver}</h3> <br></br>
              {/* <h3>{post.createdAt}</h3>
              <h3>{post.updatedAt}</h3> */}
    
            </div>)
            )}
        </>
      )  
    
}

