import React, { useState } from 'react';

import handleViewPost from '../helpers/handleViewPost';

const AuthenticatedPosts = async({posts, setPosts}) => {
    

    // const regularPosts = async() => {
        const response = await fetch("https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts")
        .then(response => response.json())
        .then(result => {
        console.log(result.data.posts);
        setPosts(result.data.posts);
      })
      .catch(console.error);
    //   regularPosts();
    
      return (
        <>
          <h1>Posts</h1>
  
          <button type="button" className="add-Btn"
           onClick={handleAddPost}>Add Post</button>
          {
            posts.map(post => <div key={post._id}>  
              <h3>{post.title}</h3>
              <h3>{post.description}</h3>
              <h3>{post.price}</h3>
              <h3>{post.location}</h3>
              <h3>{post.willDeliver}</h3>
              <h3>{post.createdAt}</h3>
              <h3>{post.updatedAt}</h3>
    
            </div>)
          }
        </>
      )  
    
}

  const handleAddPost = () => {
    const [posts, setPosts] = useState([]);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [location, setLocation] = useState("");
    const [willDeliver, setWillDeliver] = useState(false);
  
  
    const postSubmit = async () => {
      const response = await fetch("https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts", {
      method: 'POST',
      headers: {
        'Content-Type': 'Application/json',
        'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MWYzNWRjNWMxYTgyZDAwMTc3YTg0OTEiLCJ1c2VybmFtZSI6Ikphc29uIiwiaWF0IjoxNjQzMzM5MjA1fQ.bB63o6KaEkdrxNE_zu-0rWiWEudhwtd-qCOPJUoz2tA'
      },
      body: JSON.stringify({
        post: { // Is this right?
            title: title,
            description: description,
            price: price,
            location: location
              }
      })
    });
    const result = await response.json();
    const data_Posts = result.data.posts;
    
    setPosts(data_Posts);

    // View button here
    <button onClick={handleViewPost}>VIEW</button> // can I make handleViewPost as a component or function 
  
    }
    return (
      <>
        <h3>Create Post</h3>
  
        <form onSubmit={postSubmit}>
          <input type="text" placeholder = "title" value={title}
           onChange = {(event) => {setTitle(event.target.value)}}></input> 
  
          <input type="text" placeholder = "description" value={description}
           onChange = {(event) => {setDescription(event.target.value)}}></input>  
  
          <input type="text" placeholder = "price" value={price}
           onChange = {(event) => {setPrice(event.target.value)}}></input>
  
          <input type="text" placeholder = "location" value={location}
           onChange = {(event) => {setLocation(event.target.value)}}></input>
  
          <input type="checkbox" value="Deliver"
           onClick = {() => setWillDeliver(true)}></input>
  
           <label for="willDeliver">Willing to Deliver?</label> <br></br>
  
           <button type="submit" className="submitPost-Btn">Submit</button>     
  
        </form>
      </>
    )
  }

export default AuthenticatedPosts;
