import React from 'react';

const SendMessage = ({posts, setPosts, postId, setPostId}) => {
    const [content, setContent] = useState("");

    const handleMessageSubmit = async(event) => {
            event.preventDefault();
        const response = await fetch(`https://strangers-things.herokuapp.com/api/2110-VPI-WEB-PT/posts/${postId}/messages`, {
            method: 'POST',
            headers: {
                'Content-Type': 'Applicaiton/json',
                'Authorization' : 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MWYzNWRjNWMxYTgyZDAwMTc3YTg0OTEiLCJ1c2VybmFtZSI6Ikphc29uIiwiaWF0IjoxNjQzMzM5MjA1fQ.bB63o6KaEkdrxNE_zu-0rWiWEudhwtd-qCOPJUoz2tA'
            },
            body: JSON.stringify({
                message : {      // can I use this object or should i make useState i.e const [message, setMessage] = useState({}) ???
                    content: content
                }
            })
        }).then(response => response.json())
          .then(result => {
              console.log(result);
          })
          .catch(console.error);
    }
    
    return (
        <>
            <form onSubmit={handleMessageSubmit}>
                <textarea rows="5" cols="50" value={content} placeholder="Message..."
                onChange = {(event) => setContent(event.target.value)}></textarea>

                <button type="submit" className="msg-Submit-Btn">Submit</button>
            </form>
        </>
    )
}


export default SendMessage;
